mapper.update:
update member set member_name=#{member_name}, 
	birthday=#{birthday}, 
	email=#{email}, phone=#{phone}, 
	post_no=#{post_no}, address=#{address}, 
	detail_address=#{detail_address}, 
	gender=#{gender}, 
	email_alarm=#{email_alarm}

		,member_pw=#{member_pw}

where member_id=#{member_id}
